// Recebe o texto de entrada
const texto = $input.first().json.output;

// Divide o texto apenas pela sequência de barra invertida dupla (\\)
const itens = texto.split(/\\/);

// Retorna cada item como um objeto separado, filtrando itens vazios
return itens
  .filter(item => item.trim() !== "")
  .map(item => ({ item }));
